mapper.select:
SELECT id, regDate, updateDate, relTypeCode, relId, originFileName,
fileExt, typeCode, type2Code, fileExtTypeCode, fileExtType2Code,
fileNo, fileSize
FROM file
WHERE 1
AND relId IN

	#{relId}

AND relTypeCode = #{relTypeCode}
AND typeCode = #{typeCode}
AND
type2Code = #{type2Code}
AND fileNo = #{fileNo}

mapper.update:
UPDATE `file`

	updateDate = NOW(),
	relId = #{relId}

WHERE id = #{id}

mapper.select:
SELECT id, regDate, updateDate, relTypeCode, relId, originFileName,
fileExt, typeCode, type2Code, fileExtTypeCode, fileExtType2Code,
fileNo, fileSize
FROM file
WHERE 1
AND relId = #{articleIds}
AND relTypeCode = #{relTypeCode}
AND typeCode = #{typeCode}
AND
type2Code = #{type2Code}
AND fileNo = #{fileNo}

SELECT id
FROM file
WHERE 
relTypeCode = #{relTypeCode}
AND relId = #{relId}
AND typeCode = #{typeCode}
AND type2Code = #{type2Code}
AND fileNo = #{fileNo}

mapper.update:
UPDATE `file`
updateDate = NOW(), originFileName = #{originFileName}, fileExt = #{fileExt}, fileSize = #{fileSize}, fileExtTypeCode = #{fileExtTypeCode}, fileExtType2Code = #{fileExtType2Code}, body = #{body},
WHERE id = #{id}
